mapper.select:
select 
from mmall_product


        name = #{productName}


        or id = #{productId}

mapper.update:
update mmall_product set category_id=#{categoryId}, name=#{name}, subtitle=#{subtitle},
main_image=#{mainImage},sub_images=#{subImages},price={price}, stock=#{stock}, status=#{status}
where id=#{id}
